SELECT COUNT(*) AS count
FROM @{variables('scd1_source_file_name')}
WHERE @{variables('scd1_timestamp_column_name')} > (
    SELECT COALESCE(MAX(@{variables('scd1_timestamp_column_name')}), '2020-01-01 00:00:00'::timestamp)
    FROM @{variables('scd1_target_table_name')}
);

SELECT COUNT(*) AS count
FROM @{variables('scd2_source_file_name')}
WHERE @{variables('scd2_timestamp_column_name')} > (
    SELECT COALESCE(MAX(@{variables('scd2_timestamp_column_name')}), '2020-01-01 00:00:00'::timestamp)
    FROM @{variables('scd2_target_table_name')}
);

SELECT primary_key_column
FROM arshad.master_table_inc_load
WHERE target_table= '@{item().target_table}';

SELECT primary_key_column
FROM arshad.master_table_inc_load
WHERE source_table= '@{item().source_table}';

SELECT active_column_name
FROM arshad.master_table_inc_load
